<script>
    function format_action_file(action) {
        if (action.name == "Free Action") {
            return "data:image/svg+xml;base64,";
        } else if (action.name == "Reaction") {
            return "data:image/svg+xml;base64,";
        } else if (action.name == "Single Action") {
            return "data:image/svg+xml;base64,";
        } else if (action.name == "Two Actions") {
            return "data:image/svg+xml;base64,";
        } else if (action.name == "Three Actions") {
            return "data:image/svg+xml;base64,";
        }
        return action.image.image.split(".")[0] + ".png";
    }

    export let action;
    let action_file = format_action_file(action)
</script>

<img src={action_file} alt={action.name} height=15/>

<style>
    img {
        padding-left: 3px;
    }
</style>